import React from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';

const DayCell = ({ day, dayString, events, onClick }) => {
  const dayDate = new Date(day);
  return (
    <Droppable droppableId={dayString} isDropDisabled={false}>
      {(provided) => (
        <div className="day-cell" onClick={onClick} ref={provided.innerRef} {...provided.droppableProps}>
          <div className="day-number">{dayDate.getDate()}</div>
          {events.map((event, index) => (
            <Draggable key={event.id} draggableId={event.id} index={index}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                  className="event"
                  style={{ backgroundColor: event.color }}
                >
                  {event.title}
                </div>
              )}
            </Draggable>
          ))}
          {provided.placeholder}
        </div>
      )}
    </Droppable>
  );
};

export default DayCell;
