import React, { useState } from 'react';

const EventForm = ({ onSubmit, initialEvent = null, onDelete, selectedDay }) => {
  const [event, setEvent] = useState(initialEvent || { title: '', start: '', end: '', recurrence: 'none' });

  React.useEffect(() => {
    if (selectedDay) {
      const startDate = new Date(selectedDay);
      startDate.setHours(9, 0, 0);
      const endDate = new Date(selectedDay);
      endDate.setHours(10, 0, 0);

      // Format dates in local timezone
      const formatDate = (date) => {
        const year = date.getFullYear();
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const day = String(date.getDate()).padStart(2, '0');
        const hours = String(date.getHours()).padStart(2, '0');
        const minutes = String(date.getMinutes()).padStart(2, '0');
        return `${year}-${month}-${day}T${hours}:${minutes}`;
      };

      setEvent(prev => ({
        ...prev,
        start: formatDate(startDate),
        end: formatDate(endDate)
      }));
    }
  }, [selectedDay]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEvent({ ...event, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const eventWithId = {
      ...event,
      id: event.id || Date.now().toString(),
      date: event.start ? event.start.split('T')[0] : '',
    };
    onSubmit(eventWithId);
  };

  const handleDelete = () => {
    onDelete(event.id);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="title"
        value={event.title}
        onChange={handleChange}
        placeholder="Event Title"
        required
      />
      <input
        type="datetime-local"
        name="start"
        value={event.start}
        onChange={handleChange}
        required
      />
      <input
        type="datetime-local"
        name="end"
        value={event.end}
        onChange={handleChange}
        required
      />
      <select name="recurrence" value={event.recurrence} onChange={handleChange}>
        <option value="none">No Recurrence</option>
        <option value="daily">Daily</option>
        <option value="weekly">Weekly</option>
        <option value="monthly">Monthly</option>
      </select>
      <button type="submit">Save Event</button>
      {initialEvent && <button type="button" onClick={handleDelete}>Delete Event</button>}
    </form>
  );
};

export default EventForm;
