import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';

const EventForm = ({ 
  selectedDate, 
  onClose, 
  onSave, 
  onDelete, 
  event = null 
}) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    start: '',
    end: '',
    color: '#e0e0e0',
    recurrence: 'none'
  });

  useEffect(() => {
    if (event) {
      // Format the dates for the time inputs
      const startDate = new Date(event.start);
      const endDate = new Date(event.end);

      setFormData({
        title: event.title,
        description: event.description || '',
        start: format(startDate, 'HH:mm'),
        end: format(endDate, 'HH:mm'),
        color: event.color || '#e0e0e0',
        recurrence: event.recurrence || 'none'
      });
    } else {
      // For new events, set default times
      const defaultStart = format(new Date(), 'HH:mm');
      const defaultEnd = format(new Date(new Date().setHours(new Date().getHours() + 1)), 'HH:mm');

      setFormData({
        title: '',
        description: '',
        start: defaultStart,
        end: defaultEnd,
        color: '#e0e0e0',
        recurrence: 'none'
      });
    }
  }, [event]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Create start and end dates using the selected date and time
    const [startHours, startMinutes] = formData.start.split(':');
    const [endHours, endMinutes] = formData.end.split(':');

    const startDate = new Date(selectedDate);
    startDate.setHours(parseInt(startHours), parseInt(startMinutes));

    const endDate = new Date(selectedDate);
    endDate.setHours(parseInt(endHours), parseInt(endMinutes));

    // Validate end time is after start time
    if (endDate <= startDate) {
      alert('End time must be after start time');
      return;
    }

    const eventData = {
      ...formData,
      start: startDate.toISOString(),
      end: endDate.toISOString(),
      date: selectedDate.toISOString().split('T')[0]
    };

    onSave(eventData);
    onClose();
  };

  const handleDelete = () => {
    if (event && window.confirm('Are you sure you want to delete this event?')) {
      onDelete(event.id);
      onClose();
    }
  };

  return (
    <div className="event-form">
      <h3>{event ? 'Edit Event' : 'Add Event'}</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title:</label>
          <input
            type="text"
            value={formData.title}
            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
            required
          />
        </div>

        <div className="form-group">
          <label>Description:</label>
          <textarea
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          />
        </div>

        <div className="form-group">
          <label>Start Time:</label>
          <input
            type="time"
            value={formData.start}
            onChange={(e) => setFormData({ ...formData, start: e.target.value })}
            required
          />
        </div>

        <div className="form-group">
          <label>End Time:</label>
          <input
            type="time"
            value={formData.end}
            onChange={(e) => setFormData({ ...formData, end: e.target.value })}
            required
          />
        </div>

        <div className="form-group">
          <label>Color:</label>
          <input
            type="color"
            value={formData.color}
            onChange={(e) => setFormData({ ...formData, color: e.target.value })}
          />
        </div>

        <div className="form-group">
          <label>Recurrence:</label>
          <select
            value={formData.recurrence}
            onChange={(e) => setFormData({ ...formData, recurrence: e.target.value })}
          >
            <option value="none">None</option>
            <option value="daily">Daily</option>
            <option value="weekly">Weekly</option>
            <option value="monthly">Monthly</option>
          </select>
        </div>

        <div className="form-actions">
          <button type="submit" className="save-btn">
            {event ? 'Update' : 'Save'}
          </button>
          {event && (
            <button type="button" className="delete-btn" onClick={handleDelete}>
              Delete
            </button>
          )}
          <button type="button" className="cancel-btn" onClick={onClose}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EventForm;
