import React from 'react';
import DayCell from './DayCell';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const Calendar = ({ currentDate, events, onDayClick }) => {
  const daysInMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).getDate();
  const firstDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1).getDay();

  const renderDays = () => {
    const days = [];
    for (let i = 0; i < firstDayOfMonth; i++) {
      days.push(<div key={`empty-${i}`} className="day-cell empty"></div>);
    }
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(
        <DayCell
          key={day}
          day={day}
          events={events.filter(event => new Date(event.date).getDate() === day)}
          onClick={() => onDayClick(day)}
        />
      );
    }
    return days;
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const sourceDay = source.droppableId;
    const destDay = destination.droppableId;
    const eventId = result.draggableId;

    // Check for conflicts
    const event = events.find(e => e.id === eventId);
    const newStart = new Date(destDay);
    newStart.setHours(event.start.getHours(), event.start.getMinutes());
    const newEnd = new Date(destDay);
    newEnd.setHours(event.end.getHours(), event.end.getMinutes());

    const hasConflict = events.some(e => {
      if (e.id === eventId) return false;
      return (newStart < e.end && newEnd > e.start);
    });

    if (hasConflict) {
      alert('Event conflict detected!');
      return;
    }

    // Update event
    const updatedEvent = { ...event, start: newStart, end: newEnd };
    updateEvent(eventId, updatedEvent);
  };

  return (
    <div className="calendar">
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="calendar">
          {(provided) => (
            <div
              ref={provided.innerRef}
              {...provided.droppableProps}
              className="calendar-grid"
            >
              {renderDays()}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default Calendar;
